    def showReceivedMessages(self, database, user):
        messages = database.getReceivedMessages(user.getStudentName())
        startNum = 1
        
        for message in messages:
            title = "[" + str(startNum) + "] A message from " + message[0]
            self.listWidget.addItem(title)
            startNum += 1
